  test "part one" do
    assert go_floor(@input, 0) == 280
  end

  def go_floor([@open | rest], floor), do: go_floor rest, floor + 1
  def go_floor([@close | rest], floor), do: go_floor rest, floor - 1
  def go_floor([], floor), do: floor

  test "part two" do
    assert go_basement(@input, 0) == 1797
  end
  
  def go_basement(left, floor) when floor < 0, do: Enum.count(@input) - Enum.count(left)
  def go_basement([@open | rest], floor), do: go_basement rest, floor + 1
  def go_basement([@close | rest], floor), do: go_basement rest, floor - 1
  def go_basement([], floor), do: -1

