<^>><<<v<>>>^v^>v>v<<^^<<^>v<v^>>v^^v^^<<>^v>v><v^>v<^^>^<vv><vv^<>v<><^<<<vv<<v>v<^<<<<^^>v^v^^><<><^^^<v>v^^>>>vvv><>vv<>>^^v^v<<^>v^^v^>vv>^<<v<^<v^>^^<<v<^^>^v^^<^^v<<>>vv<^>>^><><>v>>v<>^<v^^><<>>>'

  test "part one" do
    assert deliver(@input, {0, 0},  %{ {0, 0} => 1}) == 2572
  end

  def deliver('>' ++ rest, {x, y}, delivered), do: deliver(rest, {x+1, y}, Map.put(delivered, {x+1, y}, Map.get(delivered, {x+1, y}, 0) + 1))
  def deliver('<' ++ rest, {x, y}, delivered), do: deliver(rest, {x-1, y}, Map.put(delivered, {x-1, y}, Map.get(delivered, {x-1, y}, 0) + 1))
  def deliver('v' ++ rest, {x, y}, delivered), do: deliver(rest, {x, y+1}, Map.put(delivered, {x, y+1}, Map.get(delivered, {x, y+1}, 0) + 1))
  def deliver('^' ++ rest, {x, y}, delivered), do: deliver(rest, {x, y-1}, Map.put(delivered, {x, y-1}, Map.get(delivered, {x, y-1}, 0) + 1))
  def deliver([], _coord, delivered), do: Enum.count delivered

  test "part two" do
    # move, but skip locations already visited
    # Santa takes even indices, Robo takes odd
    {_, _, output} = @input |> Enum.chunk_every(2) |> Enum.reduce({{0, 0}, {0, 0}, %{ {0, 0} => 2}}, fn ([s, r], {santa_coord, robo_coord, delivered}) ->
      new_santa_coord = goto([s], santa_coord)
      new_robo_coord = goto([r], robo_coord)
      delivered = update(delivered, new_santa_coord)
      delivered = update(delivered, new_robo_coord)
      {new_santa_coord, new_robo_coord, delivered}
    end)
    assert output |> Enum.count == 2631
  end

  def goto('>', {x, y}), do: {x+1, y}
  def goto('<', {x, y}), do: {x-1, y}
  def goto('v', {x, y}), do: {x, y+1}
  def goto('^', {x, y}), do: {x, y-1}

  def update(delivered, {x, y}), do: Map.put(delivered, {x, y}, Map.get(delivered, {x, y}, 0) + 1)

end
